parameters.jsCode:
// Transform the data into the format expected by ActionItemsAgent
const idea = $('GetIdeasWithoutActionItems').first().json
const repository = $('GetRepositoryDetails').first().json;
const mvpFeatures = $('GetMVPFeatures').first().json;

if (!idea || !repository) {
  return { json: { error: 'Missing idea or repository data' } };
}

// Extract tagline from overview if available
let tagline = '';
if (idea.overview && idea.overview.tagline) {
  tagline = idea.overview.tagline;
} else if (idea.overview && typeof idea.overview === 'object') {
  // Try to extract from other fields if available
  tagline = idea.title;
}

// Format the input data
const formattedData = {
  repository: {
    id: repository.id,
    name: repository.name,
    full_name: repository.full_name,
    description: repository.description,
    html_url: repository.html_url,
    stargazers_count: repository.stargazers_count,
    forks_count: repository.forks_count,
    topics: repository.topics,
    license_name: repository.license_name,
    languages: repository.languages
  },
  idea: {
    id: idea.id,
    title: idea.title,
    tagline: tagline,
    overview: idea.overview
  },
  mvpFeatures: null
};

// Add MVP features if available
if (mvpFeatures && mvpFeatures.analysis_payload) {
  try {
    const mvpFeaturesData = typeof mvpFeatures.analysis_payload === 'string' 
      ? JSON.parse(mvpFeatures.analysis_payload)
      : mvpFeatures.analysis_payload;

    formattedData.mvpFeatures = mvpFeaturesData;
  } catch (e) {
    console.log('Error parsing MVP features:', e);
    // Continue without MVP features
  }
}

return { json: formattedData };
