parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "risks": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "description": {
            "type": "string",
            "description": "Clear description of the risk"
          },
          "impact": {
            "type": "string",
            "enum": ["high", "medium", "low"],
            "description": "Severity of impact if risk materializes"
          },
          "probability": {
            "type": "string",
            "enum": ["high", "medium", "low"],
            "description": "Likelihood of risk occurring"
          },
          "mitigation": {
            "type": "string",
            "description": "Specific strategy to mitigate this risk"
          }
        }
      }
    },
    "criticalRisks": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Most critical risks requiring immediate attention"
    },
    "contingencyPlans": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Plans for worst-case scenarios"
    }
  }
}

parameters.jsCode:
// Transform the data into the format expected by RiskAnalysisAgent
const idea = $('GetIdeasWithoutRiskAnalysis').first().json;
if (!idea) {
  return { json: { error: 'No idea found' } };
}

// Extract tagline from overview if available
let tagline = '';
if (idea.overview && idea.overview.tagline) {
  tagline = idea.overview.tagline;
} else if (idea.overview && typeof idea.overview === 'object') {
  // Try to extract from other fields if available
  tagline = idea.title;
}

// Get competitive analysis if available
const competitiveAnalysis = idea.analysis_results.find(result => 
  result.analysis_type_id === 5
);

let competitiveAnalysisData = null;
if (competitiveAnalysis && competitiveAnalysis.analysis_payload) {
  try {
    competitiveAnalysisData = typeof competitiveAnalysis.analysis_payload === 'string'
      ? JSON.parse(competitiveAnalysis.analysis_payload)
      : competitiveAnalysis.analysis_payload;
  } catch (e) {
    console.log('Error parsing competitive analysis:', e);
  }
}

// Format the input data
const formattedData = {
  repository: {
    id: idea.repository.id,
    name: idea.repository.name,
    full_name: idea.repository.full_name,
    description: idea.repository.description,
    html_url: idea.repository.html_url,
    stargazers_count: idea.repository.stargazers_count,
    forks_count: idea.repository.forks_count,
    topics: idea.repository.topics,
    license_name: idea.repository.license_name,
    languages: idea.repository.languages
  },
  idea: {
    id: idea.id,
    title: idea.title,
    tagline: tagline,
    overview: idea.overview
  },
  competitiveAnalysis: competitiveAnalysisData
};

return { json: formattedData };
